'use strict';

var parchment = require('../../parchment/dist/parchment.js');

const config = {
  scope: parchment.Scope.BLOCK,
  whitelist: ['rtl']
};
const DirectionAttribute = new parchment.Attributor('direction', 'dir', config);
const DirectionClass = new parchment.ClassAttributor('direction', 'ql-direction', config);
const DirectionStyle = new parchment.StyleAttributor('direction', 'direction', config);

exports.DirectionAttribute = DirectionAttribute;
exports.DirectionClass = DirectionClass;
exports.DirectionStyle = DirectionStyle;
